            }
        }
    }

    // Request to release the port number from the NHS Port Service using the client proxy.
    private void ReleasePortNumber()
    {
        Logger.Debug(&quot;ReleasePortNumber()&quot;);

        try
        {
            Logger.Info(&quot;Releasing port number from NHS Port Service&quot;);
            var proxy = new ProxyClient();
            proxy.DeAllocatePortNumber();
        }
        catch (FaultException ex)
        {
            Logger.Info($&quot;Port number release failed from NHS Port Service.  Error description is: {ex?.Message}.  InnerException Message is: {ex?.InnerException?.Message}&quot;);
        }
        catch (Exception ex)
        {
            Logger.Info($&quot;Port number release failed from NHS Port Service.  Error description is: {ex?.Message}.  InnerException Message is: {ex?.InnerException?.Message}&quot;);
        }
    }

    //This should always work first time unless there&#39;s a very unusual circumstance where PRS assigns a port number and then it is taken 
    //by something else before NHS Credential Management gets chance to start.
    //In that circumstance, loop and get another port from PRS.
    //Keep looping until successfully started.  (In theory, if it gets through the entire range of ports in PRS and none work, 
    //then you&#39;ll get a NoPortNumberAvailable exception and then put up a message saying NHS Credential Management failed to start.  This should never happen though.)
    private void ListenToPort()
    {
        var response = false;

        while (!response)
        {
            try
            {
                var portNumber = GetPortNumber();
                var url = UrlAclPrefix + portNumber + &quot;/&quot;;
                _signalR = WebApp.Start&lt;Startup&gt;(url);
                response = true;
            }
            catch (FaultException ex)
            {
                if (ex.Code == PRSExceptions.ServiceNotAvailable || ex.Code == PRSExceptions.NoPortNumberAvailable)
                {
                    //Nothing we can do if PRS is down - just throw exception and put up message saying failed to start NHS Credential Management.
                    throw;
                }
            }
            catch (Exception ex)
            {
                //If it is a TargetInvocationException - keep looping and trying again.
                //Should only get a TargetInvocationException if the port is in use by something else.
                //If PRS is working properly, this would only happen if the port has started being used by something else in the very short period
                //of time since PRS assigned this port to this session.
                //In that rare circumstance, loop and get a different port number from PRS, and try again to start NHS Credential Management.

                //If it is any other exception, throw it and put up a message saying failed to start NHS Credential Management.
                if (!CatchTargetInvocationException(ex))
                {
                    throw;
                }
            }
        }
    }

    // Request the port number from the NHS Port Service using a client proxy. Client proxy returns the port number which is available from the configured range.
