        Logger.Debug(&quot;GetPortNumber()&quot;);

        try
        {
            Logger.Info(&quot;Getting the port number from the NHS Port Service&quot;);
            var proxy = new ProxyClient();
            var portNo = proxy.GetPortNumber();
            Logger.Info($&quot;Port no. received from the NHS Port Service {portNo}&quot;);
            _settings = new ServerSettings();
            _settings.PortNo = Convert.ToString(portNo);

            return portNo;
        }
        catch (FaultException ex)
        {
            Logger.Info($&quot;Getting Port Number from NHS Port Service failed.  Error description is: {ex?.Message}.  InnerException Message is: {ex?.InnerException?.Message}&quot;);
            throw;
        }
        catch (Exception ex)
        {
            Logger.Info($&quot;Getting Port Number from NHS Port Service failed.  Error description is: {ex?.Message}.  InnerException Message is: {ex?.InnerException?.Message}&quot;);
            throw;
        }
    }

